import{_ as s,g as n,k as a,m as t,t as i,q as r}from"./index-32af6b15.js";const o="/assets/image-banner-1-64e7535f.png",c="/assets/image-banner-2-cade9e8d.png",p="/assets/image-banner-3-56486088.png",m="/assets/image-banner-4-ce6ca115.png",b="/assets/icon-1-36ea6240.svg",g="/assets/icon-2-0e1b8145.svg",l="/assets/icon-3-a9534b58.svg",d="/assets/icon-4-6c790296.svg",f="/assets/icon-5-13daa0c5.svg",u="/assets/icon-6-c914b125.svg",_="/assets/image-performance-1-47094b6a.png",x="/assets/image-performance-2-f88d1ee7.png",h="/assets/image-performance-3-18132a82.png",I="/assets/image-performance-4-bc7652bd.png",v="/assets/image-performance-5-6257d500.png",w="/assets/image-performance-6-9a18afd9.png",E={bannerList:[{url:o},{url:c},{url:p},{url:m}],reservationIconList:[{title:"加入官方 LINE",icon:b},{title:"提供現場照片",icon:g},{title:"選擇日期",icon:l},{title:"確認付款",icon:d},{title:"到府清潔",icon:f},{title:"成果驗收",icon:u}],performanceList:[{image:_,btnText:"窗溝清潔"},{image:x,btnText:"浴室清潔"},{image:h,btnText:"水龍頭清潔"},{image:I,btnText:"乾濕分離玻璃清潔"},{image:v,btnText:"窗戶清潔"},{image:w,btnText:"廚房清潔"}]},q={contentList:[[{title:"客廳清潔",can:["沙發擦拭","地毯除塵","桌、櫃擦拭","地板清潔","燈具表面擦拭","雜物整理","垃圾打包"],cant:["沙發深層污漬","地毯深層污漬","天花板層板清潔","吊燈清潔"],estimatedTime:["0.5","1.5"],desc:["普通清潔","空間大、許久未清潔"],notice:"建議您與潔客可採用安全之方式拆下後，交由潔客做清洗。",image:"https://picsum.photos/500/178"},{title:"客廳清潔2",can:["沙發擦拭","地毯除塵","桌、櫃擦拭","地板清潔","燈具表面擦拭","雜物整理","垃圾打包"],cant:["沙發深層污漬","地毯深層污漬","天花板層板清潔","吊燈清潔"],estimatedTime:["0.5","1.5"],desc:["普通清潔","空間大、許久未清潔"],notice:"建議您與潔客可採用安全之方式拆下後，交由潔客做清洗。",image:"https://picsum.photos/500/178"},{title:"客廳清潔3",can:["沙發擦拭","地毯除塵","桌、櫃擦拭","地板清潔","燈具表面擦拭","雜物整理","垃圾打包"],cant:["沙發深層污漬","地毯深層污漬","天花板層板清潔","吊燈清潔"],estimatedTime:["0.5","1.5"],desc:["普通清潔","空間大、許久未清潔"],notice:"建議您與潔客可採用安全之方式拆下後，交由潔客做清洗。",image:"https://picsum.photos/500/178"},{title:"客廳清潔4",can:["沙發擦拭","地毯除塵","桌、櫃擦拭","地板清潔","燈具表面擦拭","雜物整理","垃圾打包"],cant:["沙發深層污漬","地毯深層污漬","天花板層板清潔","吊燈清潔"],estimatedTime:["0.5","1.5"],desc:["普通清潔","空間大、許久未清潔"],notice:"建議您與潔客可採用安全之方式拆下後，交由潔客做清洗。",image:"https://picsum.photos/500/178"}]],tabList:["居家清潔","裝潢後細清","床墊/沙發清潔","辦公室清潔"],serviceAreaList:["中區","北區","西區","南區","西區","東區","西屯區","南屯區","北屯區","大里區","太平區"]},A={questionList:[{question:"Q : 我需要在現場驗收嗎？",answer:"<span>A :<br />在服務結束前的30分鐘專員會請客戶進行驗收打掃的成果，若有需要加強、補足的地方，可以當下與清潔專員討論並進行補強。<br />若客戶不在現場或是無法於服務時間內進行檢查，視同對於此次的清潔服務無任何異議。於清潔專員離場後反應需要補足的地方，恕不負責。<br /><br />可委託他人代理驗收：若在服務時間內非訂購者在現場，須事先向立亮居家家飾清潔告知第三方（委託人）的聯絡方式，以利現場專員聯繫，避免有「客戶不在現場」的相關爭議。</span>",isExpend:!1},{question:"Q : 物品遺失與損壞賠償條約？",answer:"<span>A :<br />狀況一：預約的清潔範圍內有特殊施作方式、保養限制應事先告知本公司，未先提前預告，服務人員若做一般清潔程序而導致損壞則不再理賠範圍內。<br />狀況二：倘若發生物品損壞...等事宜，由雙方共同認定責任歸屬，而賠償的依據會考量折舊及相關法規定之，如果物品是因為自然老化等不可抗力損壞，則無法賠償。<br />狀況三：如服務過程中不慎將物品損壞或遺失，需於服務結束後12小時內告知，逾期者恕不負責任何賠償責任。<br />狀況四：若於服務過程中，使用您提供的清潔用品（清潔劑）、用具以及清潔手法而導致物品上的損壞，恕不負任何賠償責任。<br />狀況五：若於服務過程中，不慎將物品損壞，立亮居家家飾清潔為損壞的物品提供賠償，賠償上限為當次清潔訂單金額的50%。<br /></span>",isExpend:!1},{question:"Q : 是否需要自行提供清潔用具？",answer:"<span>A :<br />人員身上皆配有2組的基本清潔用具，在一天的清潔服務完畢後，會依據工具性質進行消毒，倘若您有衛生上的疑慮可於預約服務時加購清潔用具組$800元，專屬於您的用具，未來預約服務時人員到現場即可使用您加購的工具組。<br />僅需自備梯子、垃圾袋、吸塵器，若需要由我方提供請提前3個工作天告知</span>",isExpend:!1},{question:"Q : 如何修改訂單時間？",answer:"<span>A :<br />若需要修改訂單時間，需聯絡客服可透過文字(LINE@)或是電話聯繫，另外請留意手續費的收取。<br />【取消或異動】<br />＊凡訂單成立後，若改期/取消服務，將酌收訂單金額的5%為手續費。<br />＊於服務日前2～5個工作天取消/異動（不含例假日），將酌收訂單金額的30%手續費。<br />＊於服務日前一個工作天或當天取消/異動，將酌收訂單金額的50％為手續費。<br /></span>",isExpend:!1},{question:"Q : 清潔費用如何計算？",answer:"<span>A :<br />立亮居家家飾以鐘點計費，最低預約1人3小時，週末（六、日、例假日）每人每小時加收$100元的假日加成費，年節大掃除期間費用依當時活動定之，詳情將公告於官網。</span>",isExpend:!1},{question:"Q : 服務當天可以現場加時嗎？怎麼付款？",answer:"<span>A :<br />若服務當天現場人員評估超出預約時間或有增加項目，可與現場專員討論協調加時，加時費用現場支付給人員即可。<br />若現場專員無法加時，您可以在下次預約時請客服協助預約正確需要的清潔工時。</span>",isExpend:!1},{question:"Q : 服務當天可以減時嗎？",answer:"<span>A :<br />若現場專員評估有多餘時間且無其他清潔需求，2人4小時至多能減一小時，預約6-8小時至多能減2小時。<br />退款方式依據付款方式退款。</span>",isExpend:!1},{question:"Q : 服務當天人員遲到怎麼辦？",answer:"<span>A :<br />守時是我們的基本條件，服務當天人員會提前5~10分鐘至現場與您接洽，倘若預約當天人員延遲抵達現場，則以抵達現場開始計時。您隨時可向我們反應此問題，我們會加以檢討人員的出勤狀況；若您因有要事延遲抵達，則以人員抵達時間開始計算（包含等待的時間）。</span>",isExpend:!1},{question:"Q : 可以指定人員嗎？",answer:"<span>A :<br />目前還未有人員綁定的制度，而人員的安排會考量居住地點、案家之間的距離、通勤時間等。<br />若您是購買定期方案的客戶，可向客服告知您喜愛的專員姓名，我們會於系統註記此人員，往後安排預約時會儘量優先以該人員安排。</span>",isExpend:!1},{question:"Q : 車馬費怎麼計算？",answer:"<span>A :<br />超過74快速道路範圍者，皆為車馬費地段，於服務當天需另外收取車馬費$100/人補貼給清潔專員。</span>",isExpend:!1},{question:"Q : 如何預估時間？",answer:"<span>A :<br />因每人家中的整潔程度不同，對於清潔需求也不盡相同，因此您再向客服預約諮詢時，除了告知清潔需求外可以提供現場照片，進行評估、報價可降低清潔工時的落差；假如清潔範圍大、預約時間短，則建議您可以規劃清潔順序，人員將依您希望的順序評估時間內可以做到的範圍。<br />提醒您，因是以鐘點費計算實際可以施作的清潔內容依現場清潔人員評估為主。</span>",isExpend:!1},{question:"Q : 裝潢細清跟粗清的差別？",answer:"<span>A :<br />「粗清」指的是將現場的大型垃圾先回收清除，如家電的紙箱、保麗龍等大型包材，以及大量的粉塵 和 工班遺留的殘膠、殘漆，需在粗清時一併完成（以上皆不在細清的清潔範圍中）<br /><br />「細清」指的是透過專業的器材（ex : 專業吸塵器、水管），在家電、家具進場之前的清掃。<br />●以清除全區域粉塵為主，清潔後可直接開始生活。<br />●由上而下、由內到外（天花板、間接照明、冷氣外觀濾網、櫃體、牆面、門板、玻璃、窗面、窗框、窗溝等）。<br />●所有碰觸到的物體表面及櫃內間隔零件以及地板。<br /><br />流程安排 ：<br />粗清完畢 > 現場靜置3-5天的落塵期 > 細清 > 擺放個人物品 > 安心入住</span>",isExpend:!1}]},k=E,N=q,S=A;const T={name:"ChapterTitleComponent",props:{title:{type:String,default:""},white:{type:Boolean,default:!1},beige:{type:Boolean,default:!1}},setup(){return{}}},Q={class:"chapter-title"};function B(L,D,e,y,C,P){return n(),a("div",Q,[t("div",{class:r(["title",{white:e.white,beige:e.beige}])},i(e.title),3)])}const z=s(T,[["render",B],["__scopeId","data-v-325cf960"]]);export{z as C,k as h,S as n,N as s};
